//stack_top is defined in the linker script, which is automatically generated by zig build.
export fn _start() callconv(.naked) noreturn {
    asm volatile (
        \\ la sp, stack_top
        \\ call main
    );
}

export fn vector_table() linksection(".vect") callconv(.naked) noreturn {
    asm volatile (
        \\ .org  vector_table + 0*4
        \\ jal   zero, trapDefault
        \\ .org  vector_table + 1*4
        \\ jal   zero, trapDefault
        \\ .org  vector_table + 3*4
        \\ jal   zero, trapDefault
        \\ .org  vector_table + 5*4
        \\ jal   zero, trapDefault
        \\ .org  vector_table + 7*4
        \\ jal   zero, trapDefault
        \\ .org  vector_table + 9*4
        \\ jal   zero, trapDefault
        \\ .org  vector_table + 11*4
        \\ jal   zero, trapEcall
    );
}

export fn trapDefault() void {
    asm volatile (
        \\li x20, 25
        \\ebreak
    );
}

export fn trapEcall() void {
    var sys_number: u32 = 0;
    var arg1: u32 = 0;
    var arg2: u32 = 0;
    var arg3: u32 = 0;
    var arg4: u32 = 0;
    var arg5: u32 = 0;
    var arg6: u32 = 0;
    var arg7: u32 = 0;

    asm volatile (
        \\ mv %[sys_number], a7
        \\ mv %[arg1], a0;         
        \\ mv %[arg2], a1        
        \\ mv %[arg3], a2         
        \\ mv %[arg4], a3        
        \\ mv %[arg5], a4        
        \\ mv %[arg6], a5 
        \\ mv %[arg7], a6
        : [sys_number] "=r" (sys_number),
          [arg1] "=r" (arg1),
          [arg2] "=r" (arg2),
          [arg3] "=r" (arg3),
          [arg4] "=r" (arg4),
          [arg5] "=r" (arg5),
          [arg6] "=r" (arg6),
          [arg7] "=r" (arg7),
    );

    switch (sys_number) {
        40 => {
            const addr: *i32 = @ptrFromInt(0x802000 + 4);
            addr.* = 97;
        },
        else => {},
    }

    asm volatile (
        \\ csrr a0, mepc
        \\ jr a0
    );
}

export fn main() void {
    asm volatile (
        \\ li a7, 40
        \\ ecall
    );
    const addr: *i32 = @ptrFromInt(0x802000);
    addr.* = 2;
}
